mapper.update:
update user set id = #{id}

    ,username = #{username}


    ,pwd = #{pwd}


    ,last_update_user_id = #{lastUpdateUserId}


    ,server_ip = #{serverIp}


    ,version_no = #{versionNo}

where id = #{id}

mapper.select:
select * from user where is_deleted = 0

    and username = #{username}


limit 1
